window.onload=function(){for(var d=document.getElementsByClassName("acc-section"),a=0;a<d.length;a++)for(var e=d[a].getElementsByClassName("acc-title"),b=0;b<e.length;b++){var c=e[b];c.classList.add("toggle");c.nextElementSibling.classList.add("acc-closed");c.onclick=function(){this.classList.contains("toggle")?(this.classList.remove("toggle"),this.classList.add("toggle-active"),this.nextElementSibling.classList.remove("acc-closed")):(this.classList.add("toggle"),this.classList.remove("toggle-active"),this.nextElementSibling.classList.add("acc-closed"))}}};